ROWS = 20
COLS = 20

def get_neighbors(index, walls):
    neighbors = []
    r, c = divmod(index, COLS)
    directions = [(-1, 0), (1, 0), (0, 1), (0, -1)]
    for dr, dc in directions:
        nr, nc = r + dr, c + dc
        if 0 <= nr < ROWS and 0 <= nc < COLS:
            n_index = nr * COLS + nc
            if n_index not in walls:
                neighbors.append(n_index)
    return neighbors

def reconstruct_path(parent_map, end_node):
    path = []
    curr = end_node
    while curr is not None:
        path.append(curr)
        curr = parent_map.get(curr) 
    path.reverse()
    return path


def heuristic(a, b):
    r1, c1 = divmod(a, COLS)
    r2, c2 = divmod(b, COLS)
    return abs(r1 - r2) + abs(c1 - c2)